def solve():
    n, k = map(int, input().split())
    s = input()

    def calculate_cost(subsequence):
        cost = 0
        length = len(subsequence)
        for i in range(len(subsequence)):
            cost += length - i
        return cost

    def is_valid(set_S, target_size):
        counts = {}
        for char in set_S:
            counts[char] = counts.get(char, 0) + 1

        for i in range(1, len(set_S) + 1):
            if len(set_S) - i + counts.get(set_S[:i], 0) < target_size:
                return False
        return True


    def generate_subsequences(current_set_S, start_index):
        if len(current_set_S) == k:
            if is_valid(current_set_S, len(current_set_S)):
                return calculate_cost(current_set_S)
            else:
                return float('inf')

        best_cost = float('inf')

        for i in range(start_index, n + 1):
            new_set_S = current_set_S + [s[start_index i - 1]]

            cost = calculate_cost(new_set_S)
            if cost < best_cost:
                best_cost = cost
            generate_subsequences(new_set_S, i)

    start_index = 0
    generate_subsequences([s[start_index]], start_index)
    print(-1 if best_cost == float('inf') else best_cost)

solve()

