def solve():
    A, B, K = map(int, input().split())

    def find_smallest_integers(n):
        smallest_integers = []
        count = 0
        for i in range(A, B + 1):
            if len(smallest_integers) < K:
                smallest_integers.append(i)
                count += 1
                if count == K:
                    break
        return smallest_integers

    def find_largest_integers(n):
        largest_integers = []
        count = 0
        for i in range(A, B - 1, -1):
            if len(largest_integers) < K:
                largest_integers.append(i)
                count += 1
                if count == K:
                    break
        return largest_integers

    smallest_integers = find_smallest_integers(B)
    largest_integers = find_largest_integers(A)

    if not smallest_integers or not largest_integers:
        print(*list(range(A, B + 1)))
        return

    print(*smallest_integers)
    print(*largest_integers)

