def mod_inverse(a, m):
    if a == 1:
        return 1
    if a == -1:
        return -1
    if a < 0:
        a += m
    d = m
    u1 = 1
    u2 = 0
    v1 = 0
    v2 = 1
    while d > 0:
        q = a // d
        t = d
        d = a % d
        a = t
        t = v1
        v1 = u1
        u1 = v2
        v2 = t - q * u2
    if a < 0:
        a = -a
    if a == 1:
        return 1
    if a == m:
        return m
    while a > 1:
        q = a // m
        t = m
        m = a % m
        a = t
    return m

def factorial(n):
    if n == 0:
        return 1
    else:
        return n * factorial(n-1)

def ncr(n, r):
    if n < r:
        return 0
    if r == 0:
        return 1
    return factorial(n) // (factorial(r) * factorial(n - r))

