def knight_min_health(dungeon):
    m, n = len(dungeon), len(dungeon[0])
    dp = [[0] * n for _ in range(m)]
    dp[0][0] = dungeon[0][0]

    for i in range(m):
        dp[i][0] = dungeon[i][0] + dp[i-1][0]
    for j in range(n):
        dp[0][j] = dungeon[0][j] + dp[0][j-1]

    for i in range(1, m):
        for j in range(1, n):
            dp[i][j] = max(dungeon[i][j] + dp[i-1][j], dungeon[i][j] + dp[i][j-1]) + 1

    return dp[m-1][n-1]

