    def is_prime(n):
        if n <= 1:
            return False
        if n <= 3:
            return True
        if n % 2 == 0 or n % 3 == 0:
            return False
        i = 5
        while i * i <= n:
            if n % i == 0 or n % (i + 2) == 0:
                return False
            i += 6
        return True

    def prime_row_or_column(matrix, row, col):
        prime_elements = []
        for i in range(m):
            if matrix[row][i] > 0:
                prime_elements.append(matrix[row][i])
        if len(prime_elements) == 1 and is_prime(prime_elements[0]):
