        for i in range(m):
            if matrix[i][col] > 0:
                prime_elements.append(matrix[i][col])
        if len(prime_elements) == 1 and is_prime(prime_elements[0]):
            return True
        return False

    count = 0
    for i in range(n):
        for j in range(m):
            if not (prime_row_or_column(matrix, i, j) or prime_row_or_column(matrix, j, i)):
                count += 1
                matrix[i][j] += 1

    print(count)

