def is_mirror(matrix):
    n = len(matrix)
    for i in range(n):
        if matrix[i][0] != matrix[n - 1 - i][0]:
            return False
    for j in range(1, n):
        if matrix[0][j] != matrix[n - 1 - j][0]:
            return False
    for i in range(1, n):
        for j in range(1, n):
            if matrix[i][j] != matrix[i - 1][j - 1]:
                return False
    return True

