def solve():
    n = int(input())
    x, y, r = [0] * n
    for i in range(n):
        x[i], y[i], r[i] = map(int, input().split())

    ranges = []
    for i in range(n):
        ranges.append((x[i], y[i], r[i]))

    def calculate_spaceness(ranges):
        spaceness = 0
        for i in range(n):
            for j in range(i + 1, n):
                dx = ranges[i][0] - ranges[j][0]
                dy = ranges[i][1] - ranges[j][1]
                d = math.sqrt(dx * dx + dy * dy)
                if d <= r[i] + r[j]:
                    spaceness += 1
        return spaceness

    spaceness1 = calculate_spaceness(ranges)
    spaceness2 = 0
    for i in range(n):
        for j in range(i + 1, n):
            dx = ranges[i][0] - ranges[j][0]
            dy = ranges[i][1] - ranges[j][1]
            d = math.sqrt(dx * dx + dy * dy)
            if d <= r[i] + r[j]:
                spaceness2 += 1

    print((spaceness1 + spaceness2) / 2)

