    n = int(input())
    points = []
    for _ in range(n):
        x, y, z = map(int, input().split())
        points.append((x, y, z))

    def is_balanced(point1, point2):
        dx1, dy1, dz1 = point1[0] - point2[0], point1[1] - point2[1], point1[2] - point2[2]
        dx2, dy2, dz2 = point2[0] - point1[0], point2[1] - point1[1], point2[2] - point1[2]
        return (dx1 * dx2 + dy1 * dy2 + dz1 * dz2 == 0) and (dx2 * dx1 + dy2 * dy1 + dz2 * dz1 == 0)

    snapped_points = []
    for i in range(n // 2):
        # Find two points that are not in a balanced pair
        for j in range(n):
            if j != i:
                point1 = points[j]
                point2 = points[i]
                if not is_balanced(point1, point2):
                    # Find the other point in the pair
                    for k in range(n):
                        if k != i and k != j:
                            point3 = points[k]
                            if is_balanced(point1, point3) and is_balanced(point2, point3):
                                # Remove the points
                                snapped_points.append((j + 1, i + 1))
                                points.pop(j)
                                points.pop(i)
                                break

    print(*snapped_points)

