    # Find the maximum esthetic appeal
    max_appeal = max(a)
    print(max_appeal, n)

    # Find the cut-down trees
    cut_down_trees = []
    left = 0
    right = n - 1
    while left < right:
        mid = (left + right) // 2
        if a[mid] == max_appeal:
            if mid == 0 or a[mid - 1] != max_appeal:
                cut_down_trees.append(mid + 1)
                left = mid + 1
            else:
                right = mid - 1
        else:
            if a[mid] > max_appeal:
                left = mid + 1
            else:
                right = mid - 1

    print(*cut_down_trees)

