                dp[j][0] = dp[j - 1][0]

            if a[i - 1] <= b[j - 1]:
                dp[j][0] = (dp[j][0] + dp[j - 1][0]) % modulo

    for i in range(1, total_length + 1):
        for j in range(1, m):
            dp[i][j] = (dp[i - 1][j] + dp[i][j - 1]) % modulo

    print((dp[total_length][m - 1] + modulo - 1) % modulo)

