        remaining_counts = letter_counts
        for i in range(k):
            if i + 1 < k:
                remaining_counts[i+1] += remaining_counts[i] // 2
            else:
                remaining_counts[i+1] += remaining_counts[i] % 2

        # Update the maximum length
        max_length = max(max_length, max_letter_counts + remaining_counts[0])

    print(max_length)

