class Solution:
    def findDisappearingNumbers(self, nums: List[int]) -> List[int]:
        counts = {}
        for num in nums:
            counts[num] = counts.get(num, 0) + 1

        result = []
        for num in range(len(nums) + 1):
            if num not in counts or counts[num] <= 2:
                result.append(num)

        return result

