    def rotate(image):
        rotated_image = []
        for _ in range(h):
            rotated_row = ""
            for _ in range(w):
                if image[_][w - 1 - _] == '*':
                    rotated_row += '*'
                else:
                    rotated_row += image[_][w - 1 - _]
            rotated_image.append(rotated_row)
        return rotated_image

    def flip(image):
        flipped_image = []
        for _ in range(h):
            flipped_row = ""
            for _ in range(w):
                if image[w - 1 - _][_] == '*':
                    flipped_row += '*'
                else:
                    flipped_row += image[w - 1 - _][_]
            flipped_image.append(flipped_row)
        return flipped_image

    def zoom_in(image):
        zoomed_image = []
        for _ in range(h):
            zoom_row = ""
            for _ in range(w):
                factor = 2
                if _ < w // 2:
                    factor = 1
                zoom_row += "*" * factor
            zoomed_image.append(zoom_row)
        return zoomed_image

    rotated_image = rotate(image)
    flipped_image = flip(image)
    zoomed_image = zoom_in(image)

    for row in zoomed_image:
        print("".join(row))

