    for _ in range(n):
        l, r = map(int, input().split())
        lrs.append((l, r))

    def is_tree(edges):
        if not edges:
            return True

        vertices = set()
        for l, r in edges:
            vertices.add(l)
            vertices.add(r + 1)

        if len(vertices) != n:
            return False

        for l, r in edges:
            if l in vertices and r + 1 in vertices:
                return False

        return True

    edges = []
    for l, r in lrs:
        for i in range(l, r + 1):
            edges.append((l, i))
            edges.append((i + 1, r))

    if not is_tree(edges):
        print("NO")
        return

    print("YES")

