def solve():
    r, c = map(int, input().split())
    maze = []
    for _ in range(r):
        maze.append(input())

    def is_valid(x, y):
        return 0 <= x < r and 0 <= y < c

    def get_neighbors(x, y):
        neighbors = []
        if x > 0:
            neighbors.append((x - 1, y))
        if x < r - 1:
            neighbors.append((x + 1, y))
        if y > 0:
            neighbors.append((x, y - 1))
        if y < c - 1:
            neighbors.append((x, y + 1))
        return neighbors

    def is_valid_to_escape(x, y):
        if x == 0 or y == 0 or x == r - 1 or y == c - 1:
            return True
        return maze[x][y] == '/'

    def remove_walls(x, y):
        if x == 0 or y == 0 or x == r - 1 or y == c - 1:
            return 0
        if maze[x][y] == '/':
            return 1
        if maze[x][y] == '\\':
            return 1

        if is_valid(x + 1, y):
            return 1 + remove_walls(x + 1, y)
        if is_valid(x - 1, y):
            return 1 + remove_walls(x - 1, y)
        if is_valid(x, y + 1):
            return 1 + remove_walls(x, y + 1)
        if is_valid(x, y - 1):
            return 1 + remove_walls(x, y - 1)

        return 0

    total_walls = 0
    for i in range(r):
        for j in range(c):
            if is_valid_to_escape(i, j):
                total_walls += remove_walls(i, j)

    print(total_walls)

solve()

