        visited[i] = True
        for j in range(n):
            if i + 1 < n and a[i] < a[j] and j not in b and not visited[j]:
                dfs(i + 1)
            if a[i] >= a[j] and j not in b:
                ans += 1
                dfs(i + 1)

    dfs(0)

    if ans == 0:
        print(-1)
    else:
        print(ans)

