def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u - 1, v - 1))

    graph = [[] for _ in range(n)]
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def find_path(u, v):
        if u == v:
            return [u]
        paths = []
        for child in graph[u]:
            if child != v:
                path = find_path(child, v)
                if path:
                    paths.append(path + [u])
        return paths

    def find_simple_paths(u, v):
        if u == v:
            return [u]

        paths_u = []
        for child in graph[u]:
            if child != v:
                path = find_simple_paths(child, v)
                if path:
                    paths_u.append(path + [u])

        paths_v = []
        for child in graph[v]:
            if child != u:
                path = find_simple_paths(u, child)
                if path:
                    paths_v.append(path + [v])

        return paths_u + paths_v

    def find_common_paths(u, v):
        paths_u = find_path(u, v)
        paths_v = find_simple_paths(u, v)

        common_paths = []
        for path_u in paths_u:
            for path_v in paths_v:
                if path_u[0] == path_v[0]:
                    common_paths.append(path_u + [u] + path_v[1:])
        return common_paths

    u, v = find_simple_paths(0, n - 1)

    common_paths = find_common_paths(u, v)

    max_length = 0
    best_u = -1
    best_v = -1

    for path_u in common_paths:
        length = len(path_u)
        if length > max_length:
            max_length = length
            best_u = u
            best_v = v

    print(best_u + 1, best_v + 1)

solve()

