def solve():
    p, d, n = map(int, input().split())
    grid = []
    for _ in range(p):
        r, c = map(int, input().split())
        grid.append((r, c))

    def is_alive(pumpkin):
        x, y = pumpkin
        roots = 0
        for i in range(4):
            r, c = grid[x][y] + i
            if r < 0 or r >= n or c < 0 or c >= n:
                continue
            roots += 1
        return roots <= 1

    alive_count = 0
    days = 0
    while alive_count < p and days < d:
        new_alive = 0
        new_dead = 0
        for pumpkin in range(p):
            if is_alive(grid[pumpkin]):
                roots = 0
                for i in range(4):
                    r, c = grid[pumpkin][i] + i
                    if r < 0 or r >= n or c < 0 or c >= n:
                        continue
                    roots += 1
                if roots <= 1:
                    new_alive += 1
        for pumpkin in range(p):
            if not is_alive(grid[pumpkin]):
                roots = 0
                for i in range(4):
                    r, c = grid[pumpkin][i] + i
                    if r < 0 or r >= n or c < 0 or c >= n:
                        continue
                    roots += 1
                if roots > 1:
                    new_dead += 1
        if new_alive == p and new_dead == p:
            alive_count += 1
        else:
            alive_count += new_alive
            days += 1
        print(days)

