    n = int(input())
    streams = []
    for _ in range(n):
        s, d, p = map(int, input().split())
        streams.append((s, d, p))

    streams.sort(key=lambda x: x[0])

    processors = []
    for i in range(n):
        processors.append([])

    for s, d, p in streams:
        for j in range(n):
            if s + d <= processors[j][0]:
                processors[j].append((s, p))

    def can_process(streams):
        stack = []
        for s, p in streams:
            if not stack or stack[-1][0] <= s:
                stack.append((s, p))
            else:
                if len(stack) > 0:
                    processors[stack[-1][1]].remove((s, p))
                if not processors[stack[-1][1]]:
                    stack.pop()
        return len(stack) == 0

    max_priority = 0
    for i in range(n):
        if can_process(processors[i]):
            max_priority += max(processors[i], key=lambda x: x[1])[1]

    print(max_priority)

solve()

