        if n <= 1:
            return False
        if n <= 3:
            return True
        if n % 2 == 0 or n % 3 == 0:
            return False
        i = 5
        while i * i <= n:
            if n % i == 0 or n % (i + 2) == 0:
                return False
            i += 6
        return True

    def find_largest_prime_factor(n):
        i = 2
        factor = 1
        while i * i <= n:
            if n % i:
                i += 1
            else:
                n //= i
                factor *= i
        if n > 1:
            factor *= n
        return factor

    primes = []
    for d_i in d:
        if is_prime(d_i):
            primes.append(d_i)

