        return abs(x1 - x2) + abs(y1 - y2)

    def dfs(x, y, visited):
        if x == n or y == n:
            return 0

        visited.add((x, y))

        max_time = 0
        for dx, dy in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
            nx, ny = x + dx, y + dy

            if (nx, ny) in visited:
                continue

            time = distance(x, y, nx, ny)

            if time > max_time:
                max_time = time

        return max_time + 1

    max_time1 = 0
    visited1 = set()
    for x in range(n):
        if x not in visited1:
            max_time1 = max(max_time1, dfs(x, 0, visited1))

    max_time2 = 0
    visited2 = set()
    for y in range(n):
        if y not in visited2:
            max_time2 = max(max_time2, dfs(0, y, visited2))

    print(max(max_time1, max_time2))

