def modpow(a, b, mod):
    if b == 0:
        return 1
    if b % 2 == 0:
        half_pow = modpow(a, b // 2, mod)
        return (half_pow * half_pow) % mod
    else:
        half_pow = modpow(a, (b - 1) // 2, mod)
        return (a * half_pow) % mod

def modinv(a, mod):
    return modpow(a, mod - 2, mod)

