def combination(n, k):
    if n < k:
        return 0
    return factorial(n) // (factorial(k) * factorial(n - k))

def factorial(n):
    if n == 0:
        return 1
    else:
        return n * factorial(n - 1)

def gcd(a, b):
    if b == 0:
        return a
    return gcd(b, a % b)

def lcm(a, b):
    return (a * b) // gcd(a, b)

