import numpy as np

def distance(x1, y1, x2, y2):
    """Calculate the Euclidean distance between two points."""
    return np.sqrt((x2 - x1)**2 + (y2 - y1)**2)

def calculate_grip_distance(grip1, grip2):
    """Calculate the distance between two grips."""
    touch_vectors = []
    for i in range(len(grip1)):
        for j in range(len(grip1[i])):
            touch_vectors.append((grip1[i][j], grip2[i][j]))

    touch_vectors.sort()

    grip1_sum = 0
    grip2_sum = 0

    for vector in touch_vectors:
        grip1_sum += vector[0]
        grip2_sum += vector[1]

    grip1_avg = grip1_sum / len(grip1)
    grip2_avg = grip2_sum / len(grip2)

    return distance(grip1_avg, grip2_avg)

def classify_gesture(image1, image2):
    """Classify a gesture based on the distance between two grips."""
    grip1 = []
    grip2 = []

    for i in range(len(image1)):
        for j in range(len(image1[i])):
            if image1[i][j] == 'X':
                grip1.append((i, j))
            elif image1[i][j] == '.':
                grip2.append((i, j))

    grip1 = np.array(grip1)
    grip2 = np.array(grip2)

    distance1 = calculate_grip_distance(grip1, grip2)
    distance2 = calculate_grip_distance(grip2, grip1)

    if distance1 > distance2:
        return "pan", "out"
    elif distance1 < distance2:
        return "zoom", "in"
    else:
        return "rotate", "out"

