        visited[node] = True
        arrow_positions.append(node)
        for neighbor in graph[node]:
            dfs(neighbor, position + 1)

    for i in range(1, n + 1):
        if not visited[i]:
            dfs(i, 1)

    arrow_positions.reverse()

    if len(arrow_positions) != k:
        print("Impossible")
    else:
        print(*arrow_positions)

