        roads.append((o - 1, d - 1, l))

    mod = 1000000007
    graph = [[] for _ in range(n)]
    for u, v, w in roads:
        graph[u].append((v, w))
        graph[v].append((u, w))

    dist = [float('inf')] * n
    dist[0] = 0
    queue = [(0, 0)]
    visited = set()
    while queue:
        curr_dist, curr_node = queue.pop(0)
        if curr_dist > dist[curr_node]:
            continue
        for neighbor, weight in graph[curr_node]:
            if neighbor in visited:
                continue
            visited.add(neighbor)
            queue.append((curr_dist + weight, neighbor))
            dist[neighbor] = curr_dist + weight

    ans = []
    for u, v, w in roads:
        if dist[u] + dist[v] == dist[u] + dist[v] + w:
            ans.append((mod - 1) * (mod + 1) % mod)
        else:
            ans.append(1)
    print(*ans)

