import math

def is_squarefree(n):
    """Check if a number is squarefree."""
    if n < 2:
        return True
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def find_smallest_m(n):
    """Find the smallest m such that m * n is squarefree."""
    m = 2
    while True:
        if is_squarefree(m * n):
            return m
        m += 1

