def solve():
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v = map(int, input().split())
        edges.append((u - 1, v - 1))

    graph = [[] for _ in range(n)]
    for u, v in edges:
        graph[u].append(v - 1)
        graph[v - 1].append(u - 1)

    visited = [False] * n
    stack = [0]
    while stack:
        u = stack.pop()
        if not visited[u]:
            visited[u] = True
            stack.append(~u)
            for v in graph[u]:
                if not visited[v]:
                    stack.append(v)

    orientation = []
    for i in range(m):
        if not visited[i]:
            u = i
            v = ~i
            path = []
            while u != v:
                path.append(u)
                u = ~graph[u].index(v)
                v = ~u
            path.append(u)
            path.reverse()
            orientation.append(''.join(map(str, path)))

    if len(orientation) == m:
        print("YES")
        print("".join(orientation))
    else:
        print("NO")

solve()

