    umbrellas.sort()

    def can_make_it():
        if a == 0:
            return True

        total_fatigue = 0
        umbrella_count = 0
        current_position = 0
        umbrella = None

        for i in range(n):
            l, r = rain[i]

            if current_position == 0:
                if l <= a:
                    current_position = l
                else:
                    return False

            if current_position > r:
                if umbrella is not None:
                    total_fatigue += umbrella * (r - current_position + 1)
                    umbrella_count -= 1
                    umbrella = None
                else:
