def solve():
    n = int(input())
    numbers = []
    for _ in range(n):
        numbers.append(int(input()))

    count_dict = {}
    for num in numbers:
        if num in count_dict:
            count_dict[num] += 1
        else:
            count_dict[num] = 1

    missing_numbers = []
    last_num = numbers[-1] + 1

    for num in sorted(count_dict.keys()):
        if num > last_num:
            missing_numbers.append(num)
        else:
            count = count_dict[num]
            if count == 1:
                missing_numbers.append(num)
            else:
                missing_numbers.append(num + (last_num - num - 1))

    if not missing_numbers:
        print("good job")
    else:
        print(*missing_numbers)

solve()

