def solve():
    a, b, k = map(int, input().split())

    min_val = a
    max_val = b

    while True:
        mid = (min_val + max_val) // 2

        count = 0
        for i in range(min_val, mid + 1):
            if i >= a and i <= b:
                count += 1

        if count >= k:
            min_val = mid + 1
        else:
            max_val = mid - 1

        if min_val > b:
            break

    for i in range(min_val, max_val + 1):
        if i >= a and i <= b:
            print(i)

solve()
