from collections import deque


def bfs(graph, source, target):
    visited = set()
    queue = deque([(source, 0)])
    while queue:
        node, dist = queue.popleft()
        if node == target:
            return dist
        if node in visited:
            continue
        visited.add(node)
        for child, weight in graph[node]:
            if child not in visited:
                queue.append((child, dist + weight))
    return -1


