def multiply(a, b):
    c = ''
    i = 0
    m = len(a)
    l = len(b)
    while i < m + l - 1:
        if i < m:
            if i >= l:
                c += a[i]
            else:
                if a[i] == b[i]:
                    c += a[i]
                else:
                    c += a[i]
                    i -= 1
        else:
            c += b[i - m]
    i = 0
    maxL = 0
    while i < len(c) - 1:
        if c[i] == c[i + 1]:
            maxL = max(maxL, i + 2)
        else:
            i += 1
    return c[:maxL]

