def getbase(n):
    if n == 2:
        return 1
    if n == 3:
        return 2
    return getbase(n - 1) * n - 1


def solve(n, m, k, grid):
    dp = [[([0] * k) for _ in range(m)] for _ in range(n)]
    for i in range(n - 1, -1, -1):
        for j in range(m - 1, -1, -1):
            for l in range(k - 1, -1, -1):
                if i < n - 1 and grid[i + 1][j][l] == '1':
                    dp[i][j][l] |= 1 << getbase(n - i - 1)
                if j < m - 1 and grid[i][j + 1][l] == '1':
                    dp[i][j][l] |= 1 << getbase(m - j - 1)
                if l < k - 1 and grid[i][j][l + 1] == '1':
                    dp[i][j][l] |= 1 << getbase(k - l - 1)
    res = 0
    for i in range(n):
        for j in range(m):
            for l in range(k):
                if grid[i][j][l] == '1':
                    res |= dp[i][j][l]
    return res


