def getIntersectArea(x1, y1, x2, y2, x3, y3, x4, y4):
    area12 = getArea(x1, y1, x2, y2)
    area13 = getArea(x1, y1, x3, y3)
    area14 = getArea(x1, y1, x4, y4)
    area23 = getArea(x2, y2, x3, y3)
    area24 = getArea(x2, y2, x4, y4)
    area34 = getArea(x3, y3, x4, y4)
    return area12 + area13 + area14 - area23 - area24 + area34


