class BIT:

    def __init__(self, n):
        self.n = n
        self.bit = [0] * (n + 1)

    def query(self, i):
        i = i + 1
        s = 0
        while i <= self.n:
            s = s + self.bit[i]
            i = i + (i & -i)
        return s

    def update(self, i, x):
        i = i + 1
        while i <= self.n:
            self.bit[i] = self.bit[i] + x
            i = i + (i & -i)


def getsum(bit, i, j):
    return bit.query(j) - bit.query(i - 1)


