def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0]) ** 2 + (p1[1] - p2[1]) ** 2)


def angle(p1, p2):
    return math.atan2(p2[1] - p1[1], p2[0] - p1[0])


def rotate(p1, p2):
    return angle(p2, p1)


def clockwise(p1, p2):
    return rotate(p1, p2) > 0


def count_touches(image):
    touch_count = 0
    touch_x = []
    touch_y = []
    for y, row in enumerate(image):
        for x, pixel in enumerate(row):
            if pixel == 'X':
                touch_count += 1
                touch_x.append(x)
                touch_y.append(y)
    return touch_count, touch_x, touch_y


def calculate_grip_point(touch_x, touch_y, image):
    grip_x = sum(touch_x) / touch_count
    grip_y = sum(touch_y) / touch_count
    return grip_x, grip_y


def calculate_touch_vector(grip_point, touch_point):
    return (touch_point[0] - grip_point[0], touch_point[1] - grip_point[1])


def calculate_grip_point(image):
    touch_x, touch_y, _ = count_touches(image)
    grip_point = calculate_grip_point(touch_x, touch_y, image)
    return grip_point


def calculate_touch_vectors(grip_point, touch_x, touch_y):
    touch_vectors = []
    for i in range(touch_count):
        touch_vectors.append(calculate_touch_vector(grip_point, (touch_x[i],
            touch_y[i])))
    return touch_vectors


def calculate_grip_spread(touch_vectors):
    touch_points = []
    for vector in touch_vectors:
        touch_points.append((vector[0] ** 2 + vector[1] ** 2) ** 0.5)
    return sum(touch_points) / touch_count


def calculate_rotation(grip_point, touch_vectors):
    rotation = 0
    for i in range(touch_count):
        rotation += clockwise(grip_point, touch_vectors[i])
    return rotation / touch_count


def classify_gesture(initial_image, final_image):
    initial_grip_point = calculate_grip_point(initial_image)
    final_grip_point = calculate_grip_point(final_image)
    initial_touch_vectors = calculate_touch_vectors(initial_grip_point,
        initial_x, initial_y)
    final_touch_vectors = calculate_touch_vectors(final_grip_point,
        final_x, final_y)
    initial_grip_spread = calculate_grip_spread(initial_touch_vectors)
    final_grip_spread = calculate_grip_spread(final_touch_vectors)
    rotation = calculate_rotation(initial_grip_point, initial_touch_vectors)
    if abs(final_grip_spread - initial_grip_spread) > 1e-5:
        distance = abs(final_grip_spread - initial_grip_spread)
    else:
        distance = abs(rotation)
    if distance > pan_distance:
        print(touch_count, 'rotate')
        if final_grip_spread < initial_grip_spread:
            print('out')
        else:
            print('in')
    elif distance > zoom_distance:
        print(touch_count, 'pan')
    else:
        print(touch_count, 'zoom')
        if final_grip_spread < initial_grip_spread:
            print('out')
        else:
            print('in')
    return distance


