def sum_of_digits(n):
    return sum([int(i) for i in str(n)])


def sum_between_A_and_B(n, A, B):
    sum = 0
    for i in range(1, n+1):
        if A <= sum_of_digits(i) <= B:
            sum += i
    return sum


