import math


def is_perfect_power(n):
    if n == 1:
        return True
    for base in range(2, int(math.sqrt(n)) + 1):
        exp = 2
        while n > 1:
            if n == base ** exp:
                return True
            else:
                n = n % base
                exp = exp + 1
    return False


