def isPrime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 3 == 0:
        return False
    elif n % 2 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i = i + 6
    return True


def getPrimeFactors(n):
    primeFactors = []
    if n % 2 == 0:
        primeFactors.append(2)
    while n % 2 == 0):
        n = n / 2
    for i in range(3, int(n ** 0.5) + 1, 2):
        if isPrime(i):
            while n % i == 0:
                primeFactors.append(i)
                n = n / i
    if n > 2:
        primeFactors.append(int(n))
    return primeFactors


