class Solution:

    def calculateMinimumHealtH(self, M: int, N: int, dungeon: List[List[int]]) ->int:
        dp = [([0] * N) for _ in range(M)]

        def solve(r, c):
            if r == M:
                if c == N:
                    return 0
            if r >= M or c >= N:
                return float('inf')
            if dp[r][c] != 0:
                return dp[r][c]
            health_loss = dungeon[r][c]
            if r + 1 < M:
                health_loss += solve(r + 1, c)
            if c + 1 < N:
                health_loss += solve(r, c + 1)
            dp[r][c] = health_loss
            return health_loss
        return solve(0, 0)

