def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True


def check_matrix(m):
    for row in m:
        if not all(is_prime(x) for x in row):
            return False
    return True


def check_cols(m):
    for j in range(len(m[0])):
        col = [row[j] for row in m]
        if not all(is_prime(x) for x in col):
            return False
    return True


