def solve():
    n = int(input())
    permutation = list(map(int, input().split()))
    visited = [False] * n
    cycles = []

    def walk(current_position, cycle):
        visited[current_position] = True
        cycle.append(current_position)
        next_position = permutation[current_position] - 1
        if not visited[next_position]:
            walk(next_position, cycle)
        else:
            found_start = False
            for i in range(len(cycle)):
                if cycle[i] == next_position:
                    found_start = True
                    break
            if not found_start:
                cycle.append(next_position)
                walk(next_position, cycle)
    for i in range(n):
        if not visited[i]:
            cycle = []
            walk(i, cycle)
            cycles.append(cycle)
    print(len(cycles))
    for i in range(len(cycles)):
        print(*cycles[i])


