def get_center(point):
    return tuple(point) if isinstance(point, tuple) else point

def get_radius(point):
    return point[2] if isinstance(point, tuple) else point

def get_bounds(point):
    center = get_center(point)
    radius = get_radius(point)
    return (center[0] - radius, center[0] + radius), (center[1] - radius, center[1] + radius)

def intersect(a_b, c_d):
    x1, y1, x2, y2 = a_b
    cx, cy, cx2, cy2 = c_d
    if x2 < cx or x1 > cx2 or y2 < cy or y1 > cy2:
        return False
    return True

def cover_points(points, covered):
    for point in points:
        covered[get_center(point)] = True
        get_bounds(point)
        for other_center in covered:
            if intersect(get_bounds(point), get_bounds(other_center)):
                covered[other_center] = True
    return covered

