def count_valid_decks(n, k, a):
    factors = []
    p = 2
    while p * p <= k:
        if k % p == 0:
            factors.append(p)
            while k % p == 0:
                k //= p
        p += 1
    if k > 1:
        factors.append(k)
    if not len(factors) == 1:
        valid_counts = {}
        for f in factors:
            valid_counts[f] = 0
        for i in range(len(a) - 1):
            mult = 1
            for j in range(i, len(factors)):
                if a[i] % factors[j] == 0:
                    mult *= factors[j]
            if mult % k == 0:
                valid_counts[factors[j]] += 1
        print(sum(x for x in valid_counts.values() if x > 0))
    else:
        print(0)


