                            for edge in adj[a]:
                                if edge[0] == b:
                                    residual_capacity = edge[1]
                        for i in range(len(path) - 1):
                            a, b = path[i], path[i + 1]
                            for j in range(len(adj[a])):
                                if adj[a][j][0] == b:
                                    adj[a][j] = (adj[a][j][0], residual_capacity)
                                    break
                                    adj[b].append((a, 0))
        find_path(parent, start, end)
    def find_path(parent, start, end):
        path = []
        curr = end
        while curr != -1:
            path.append(curr)
            curr = parent[curr]
        return path, 0
        for i in range(len(path) - 1):
            a, b = path[i], path[i + 1]
            for edge in adj[a]:
                if edge[0] == b:
                    return path, min(edge[1], adj[b][0][1])
