        nonlocal max_satisfaction
        visited[node] = True
        path.append(node)
        for neighbor in graph[node]:
            if neighbor not in path:
                if not visited[neighbor]:
                    dfs(neighbor, current_satisfaction + c[neighbor] * (
                        1 / (2 ** (path.index(neighbor) - 1)) if path.index(
                        neighbor) > 0 else 1), path)
        path.pop()
        visited[node] = False
