        edges.append((u - 1, v - 1))

    def dfs_order(node, parents):
        children = []
        for u, v in edges:
            if u == node or v == node:
                if u != node:
                    children.append(dfs_order(u, parents))
                if v != node:
                    children.append(dfs_order(v, parents))
        if not children:
            return [node]
        import random
        random.shuffle(children)
        return [node] + children

