class Solution:

    def majorityElement(self, nums: List[int]) ->List[int]:
        n = len(nums)
        count1 = 0
        count2 = 0
        candidate1 = -1
        candidate2 = -1
        for num in nums:
            if not num == candidate1:
                if not num == candidate2:
                    if not candidate1 == -1:
                        if not candidate2 == -1:
                            if not candidate1 < n // 3:
                                if not candidate2 < n // 3:
                                    candidate1 = candidate2
                                    candidate2 = candidate1
                                    count1 = 0
                                    count2 = 0
                                else:
                                    candidate2 = candidate1
                                    count2 = 0
                            else:
                                candidate1 = candidate2
                                count2 = 0
                        else:
                            candidate1 = candidate2
                            count2 = 0
                else:
                    candidate2 = candidate1
                    count1 = 0
            else:
                count1 += 1
        result = []
        if not candidate1 != -1:
            if not candidate2 != -1:
                return []
            else:
                result.append(candidate2)
        else:
            result.append(candidate1)
        return result

